"use client"

import { cn } from "@/lib/utils"
import React, {
  forwardRef,
  useRef,
  type HTMLInputTypeAttribute,
  memo,
} from "react"
import {
  CellsContextProvider,
  useCellsContext,
} from "@/hooks/use-cells"
import { CellContextProvider, useCellContext } from "@/hooks/use-cell"

interface CellsProps extends React.ComponentPropsWithoutRef<"form"> {
  className?: string
  children: React.ReactNode
}

export const Cells = forwardRef<HTMLFormElement, CellsProps>(
  ({ className, children, ...props }, ref) => (
    <CellsContextProvider aria-multiselectable="true">
      <CellsForm className={cn("", className)} {...props} ref={ref}>
        {children}
      </CellsForm>
    </CellsContextProvider>
  ),
)
Cells.displayName = "Cells"

const _renderCells = ({
  children,
  parentRowIndex,
  addCellIndex,
}: {
  children: React.ReactNode
  parentRowIndex?: number
  addCellIndex: (
    rowIndex: number,
    cellIndex: number,
    ref: React.RefObject<HTMLInputElement>,
    initialValue: string,
  ) => void
}) => {
  if (!children) throw new Error("No children provided")

  let rowIndex = parentRowIndex ?? 0
  let cellIndex = 0

  return React.Children.map(children, (child) => {
    if (!React.isValidElement(child)) return null

    if (parentRowIndex === undefined) {
      if (
        typeof child.type === "function" &&
        (child.type as React.ComponentType).displayName !== "CellRow"
      ) {
        throw new Error("Invalid child type, only CellRow is allowed")
      }

      const tmpKey = child.key ? child.key : rowIndex
      rowIndex++

      return (
        <CellRow key={tmpKey}>
          {_renderCells({
            children: child.props.children,
            parentRowIndex: rowIndex - 1,
            addCellIndex,
          })}
        </CellRow>
      )
    }

    if (
      typeof child.type === "function" &&
      (child.type as React.ComponentType).displayName !== "Cell"
    ) {
      throw new Error("Invalid child type, only Cell is allowed")
    }

    const childRef = useRef<HTMLInputElement>(null)
    const initialValue = child.props.initialValue || ""

    addCellIndex(rowIndex, cellIndex, childRef, initialValue)
    cellIndex++

    return (
      <CellContextProvider rowIndex={rowIndex} cellIndex={cellIndex - 1}>
        <Cell
          cellIndex={cellIndex - 1}
          rowIndex={rowIndex}
          ref={childRef}
          {...child.props}
        >
          {child.props.children}
        </Cell>
      </CellContextProvider>
    )
  })
}

interface CellsForm extends React.ComponentPropsWithoutRef<"form"> {
  className?: string
  children: React.ReactNode
}

const CellsForm = forwardRef<HTMLFormElement, CellsForm>(
  ({ className, children, ...props }, ref) => {
    const { addCellIndex } = useCellsContext()

    return (
      <form className={cn("", className)} role="grid" {...props} ref={ref}>
        {_renderCells({ children, addCellIndex })}
      </form>
    )
  },
)
CellsForm.displayName = "CellsForm"

interface CellRowProps extends React.ComponentPropsWithoutRef<"div"> {
  className?: string
  children: React.ReactNode
}

export const CellRow = forwardRef<HTMLDivElement, CellRowProps>(
  ({ className, children, ...props }, ref) => {
    return (
      <div
        className={cn(
          "flex flex-row border-b border-l border-r first:border-t",
          className,
        )}
        role="row"
        {...props}
        ref={ref}
      >
        {children}
      </div>
    )
  },
)
CellRow.displayName = "CellRow"

interface CellProps extends React.ComponentPropsWithoutRef<"input"> {
  name: string
  label: string
  initialValue: string
  autoselect?: boolean
  type?: HTMLInputTypeAttribute
  cellIndex?: number
  rowIndex?: number
  className?: string
}

export const Cell = memo(
  forwardRef<HTMLInputElement, CellProps>(
    (
      {
        type = "text",
        name,
        label,
        initialValue,
        autoselect,
        cellIndex,
        rowIndex,
        className,
        ...props
      },
      ref,
    ) => {
      if (cellIndex === undefined || rowIndex === undefined || !initialValue)
        throw new Error(
          "cellIndex, rowIndex and initialValue are required props for Cell",
        )

      const {
        cellValue,
        isSelected,
        isEditing,
        isActive,
        handleBlur,
        handleFocus,
        handleChange,
        handleKeyDown,
        handlePointerDown,
        handlePointerEnter,
        handleDoubleClick,
      } = useCellContext()

      return (
        <div
          tabIndex={0}
          className={cn(
            className,
            "w-20 min-w-4 cursor-pointer bg-background p-0.5 text-center [appearance:textfield] hover:inner-border-2 focus:bg-primary/5 focus:outline-none focus:inner-border-2 focus:inner-border-primary has-[:focus]:bg-primary/5 has-[:focus]:inner-border-2 has-[:focus]:inner-border-primary data-[is-selected=true]:bg-primary/5 data-[is-selected=true]:inner-border-2 [&:not(:focus)]:data-[is-selected=true]:inner-border-primary/25 [&:not(:last-child)]:border-r",
          )}
          role="gridcell"
          aria-colindex={cellIndex + 1} // ARIA indices are 1 based, not 0
          aria-rowindex={rowIndex + 1} // ARIA indices are 1 based, not 0
          aria-selected={isSelected}
          aria-label={`${label}: ${cellValue}`}
          aria-expanded={isEditing}
          data-is-editing={isEditing}
          data-is-active={isActive}
          data-is-selected={isSelected}
          onKeyDown={handleKeyDown}
          onPointerDown={handlePointerDown}
          onPointerEnter={handlePointerEnter}
          onDoubleClick={handleDoubleClick}
          onBlur={handleBlur}
          ref={ref}
          autoFocus={autoselect}
        >
          <label htmlFor={name} className="sr-only">
            {label}
          </label>
          <input
            type={type}
            name={name}
            className="w-full bg-transparent px-3 py-2 text-center outline-none [appearance:textfield] focus:inner-border-2 focus:inner-border-primary/25 [&:not(:focus)]:cursor-pointer"
            aria-hidden={!isEditing}
            value={cellValue}
            tabIndex={-1}
            onChange={handleChange}
            onKeyDown={handleKeyDown}
            onFocus={handleFocus}
            onBlur={handleBlur}
            {...props}
          />
        </div>
      )
    },
  ),
)
Cell.displayName = "Cell"

import {
  createContext,
  useContext,
  useEffect,
  useRef,
  useState,
  useMemo,
} from "react"

export type CellTraverseDirection = "left" | "right" | "up" | "down"

interface CellState {
  value: string
  isSelected: boolean
  isActive: boolean
  ref: React.RefObject<HTMLDivElement>
}

type CellsMap = Map<number, Map<number, CellState>>

interface CellsContextType {
  // Cell Management
  getCellState: (rowIndex: number, cellIndex: number) => CellState | undefined
  getRowMap: (rowIndex: number) => Map<number, CellState> | undefined
  getCellsMap: () => CellsMap
  setCellValue: (rowIndex: number, cellIndex: number, value: string) => void
  addCellIndex: (
    rowIndex: number,
    cellIndex: number,
    ref: React.RefObject<HTMLDivElement>,
    initialValue: string,
  ) => void

  // Active Cell Management
  isActiveCell: (rowIndex: number, cellIndex: number) => boolean
  setActiveCell: (rowIndex: number, cellIndex: number) => void
  setInputFocus: (rowIndex: number, cellIndex: number) => void

  // Navigation
  setNextActiveCell: ({
    direction,
    currentRowIndex,
    currentCellIndex,
    isShiftHeld,
    isCtrlHeld,
  }: {
    direction: CellTraverseDirection
    currentRowIndex: number
    currentCellIndex: number
    isShiftHeld?: boolean
    isCtrlHeld?: boolean
  }) => void

  // Selection
  isSelectedCell: (rowIndex: number, cellIndex: number) => boolean
  toggleSelectedCell: (rowIndex: number, cellIndex: number) => void
  clearSelectedCells: () => void
  clearSelectedCellsValue: () => void
  selectAllCells: () => void
  selectAllCellsInRow: (rowIndex: number) => void
  selectAllCellsInColumn: (cellIndex: number) => void

  // Mouse and Shift Selection
  handleMouseSelectStart: (rowIndex: number, cellIndex: number) => void
  handleMouseSelectMove: (rowIndex: number, cellIndex: number) => void
  handleShiftClickCell: (rowIndex: number, cellIndex: number) => void

  // Traverse Markers
  startShiftTraverse: ({
    rowIndex,
    cellIndex,
  }: {
    rowIndex: number
    cellIndex: number
  }) => void
}

const CellsContext = createContext<CellsContextType>({
  // Cell Management
  getCellState: () => undefined,
  getRowMap: () => undefined,
  getCellsMap: () => new Map(),
  setCellValue: () => {},
  addCellIndex: () => {},

  // Active Cell Management
  isActiveCell: () => false,
  setActiveCell: () => {},
  setInputFocus: () => {},

  // Navigation
  setNextActiveCell: () => undefined,

  // Selection
  isSelectedCell: () => false,
  toggleSelectedCell: () => {},
  clearSelectedCells: () => {},
  clearSelectedCellsValue: () => {},
  selectAllCells: () => {},
  selectAllCellsInRow: () => {},
  selectAllCellsInColumn: () => {},

  // Mouse and Shift Selection
  handleMouseSelectStart: () => {},
  handleMouseSelectMove: () => {},
  handleShiftClickCell: () => {},

  // Traverse Markers
  startShiftTraverse: () => {},
})

export const useCellsContext = () => {
  const context = useContext(CellsContext)

  if (!context) {
    throw new Error(
      "useCellsContext must be used within a CellsContextProvider",
    )
  }

  return context
}

interface CellsContextProviderProps {
  children: React.ReactElement
}

export const CellsContextProvider = ({
  children,
}: CellsContextProviderProps) => {
  const [cellValues, setCellValues] = useState<
    Map<number, Map<number, string>>
  >(new Map())
  const cellsMap = useRef<CellsMap>(new Map())
  const pendingInitializations = useRef<
    Array<{
      rowIndex: number
      cellIndex: number
      value: string
    }>
  >([])
  const selectionState = useRef({
    shiftTraverseMarker: undefined as
      | { rowIndex: number; cellIndex: number }
      | undefined,
    mouseSelectStartCell: undefined as
      | { rowIndex: number; cellIndex: number }
      | undefined,
  })

  // Cell Management

  const getCellState = (
    rowIndex: number,
    cellIndex: number,
  ): CellState | undefined => {
    const cell = cellsMap.current.get(rowIndex)?.get(cellIndex)
    if (cell) {
      const value = cellValues.get(rowIndex)?.get(cellIndex) ?? cell.value
      return { ...cell, value }
    }
    return undefined
  }

  const getRowMap = (rowIndex: number) => {
    return cellsMap.current.get(rowIndex)
  }

  const getCellsMap = () => {
    return cellsMap.current
  }

  const setCellValue = (rowIndex: number, cellIndex: number, value: string) => {
    const cell = getCellState(rowIndex, cellIndex)
    if (cell) {
      cell.value = value
      setCellValues((prev) => {
        const newValues = new Map(prev)
        if (!newValues.has(rowIndex)) {
          newValues.set(rowIndex, new Map())
        }
        newValues.get(rowIndex)?.set(cellIndex, value)
        return newValues
      })
    }
  }

  const clearCellsValue = (
    cells: Array<{ rowIndex: number; cellIndex: number }>,
  ) => {
    cells.forEach(({ rowIndex, cellIndex }) => {
      setCellValue(rowIndex, cellIndex, "")
    })
  }

  const addCellIndex = (
    rowIndex: number,
    cellIndex: number,
    ref: React.RefObject<HTMLDivElement>,
    initialValue: string,
  ) => {
    if (!cellsMap.current.has(rowIndex)) {
      cellsMap.current.set(rowIndex, new Map())
    }

    cellsMap.current.get(rowIndex)?.set(cellIndex, {
      value: initialValue,
      isSelected: false,
      isActive: false,
      ref,
    })

    // Queue the initialization instead of updating state directly
    pendingInitializations.current.push({
      rowIndex,
      cellIndex,
      value: initialValue,
    })
  }

  const getCellRef = (rowIndex: number, cellIndex: number) => {
    return getRowMap(rowIndex)?.get(cellIndex)?.ref
  }

  // Active Cell Management

  const getActiveCell = () => {
    for (const [rowIndex, row] of cellsMap.current.entries()) {
      for (const [cellIndex, cell] of row.entries()) {
        if (cell.isActive) {
          return { rowIndex, cellIndex }
        }
      }
    }
    return undefined
  }

  const setActiveCell = (rowIndex: number, cellIndex: number) => {
    // Clear previous active cell and set new one in a single pass
    cellsMap.current.forEach((row) => {
      row.forEach((cell) => {
        cell.isActive = false
        cell.ref?.current?.setAttribute("data-is-active", "false")
      })
    })

    const cell = getCellState(rowIndex, cellIndex)

    if (cell) {
      const cellRef = cell.ref.current
      clearShiftTraverseMarker()
      cell.isActive = true
      cellRef?.setAttribute("data-is-active", "true")
      cellRef?.focus()
    }
  }

  const isActiveCell = (rowIndex: number, cellIndex: number) => {
    return getCellState(rowIndex, cellIndex)?.isActive ?? false
  }

  const setInputFocus = (rowIndex: number, cellIndex: number) => {
    const cell = getCellState(rowIndex, cellIndex)

    const inputRef = cell?.ref.current?.querySelector("input")

    if (inputRef) {
      inputRef.focus()
      const inputCharLength = inputRef.value.length
      inputRef.setSelectionRange(inputCharLength, inputCharLength)
    }
  }

  // Navigation

  const getBoundary = (
    type: "row" | "cell",
    rowIndex: number,
    boundary: "first" | "last",
  ) => {
    if (type === "row") {
      return boundary === "first" ? 0 : getCellsMap().size - 1
    }

    const row = getRowMap(rowIndex)
    if (!row) return undefined

    return boundary === "first"
      ? Number(row.entries().next().value?.[0])
      : Number(Array.from(row.entries()).pop()?.[0])
  }

  const setNextActiveCell = ({
    direction,
    currentRowIndex,
    currentCellIndex,
    isShiftHeld,
    isCtrlHeld,
  }: {
    direction: CellTraverseDirection
    currentRowIndex: number
    currentCellIndex: number
    isShiftHeld?: boolean
    isCtrlHeld?: boolean
  }) => {
    const currentShiftTraverseMarker = getShiftTraverseMarker()
    const currentTraverseMarker = {
      cellIndex:
        isShiftHeld && currentShiftTraverseMarker
          ? currentShiftTraverseMarker.cellIndex
          : currentCellIndex,
      rowIndex:
        isShiftHeld && currentShiftTraverseMarker
          ? currentShiftTraverseMarker.rowIndex
          : currentRowIndex,
    }

    const handleHorizontalMovement = () => {
      const nextCellIndex = isCtrlHeld
        ? getBoundary(
            "cell",
            currentTraverseMarker.rowIndex,
            direction === "left" ? "first" : "last",
          )
        : ["left", "up"].includes(direction)
          ? currentTraverseMarker.cellIndex - 1
          : currentTraverseMarker.cellIndex + 1

      if (nextCellIndex === undefined) {
        if (!isShiftHeld) {
          clearSelectedCells()
        }
        return
      }

      if (isShiftHeld) {
        const boundaryCellIndex = getBoundary(
          "cell",
          currentTraverseMarker.rowIndex,
          direction === "left" ? "first" : "last",
        )
        if (boundaryCellIndex === undefined) return

        const endCellIndex = isCtrlHeld ? boundaryCellIndex : nextCellIndex
        const cellRef = getCellRef(currentTraverseMarker.rowIndex, endCellIndex)
        if (cellRef) {
          setSelectedCellRange({
            startRowIndex: currentRowIndex,
            startCellIndex: currentCellIndex,
            endRowIndex: currentTraverseMarker.rowIndex,
            endCellIndex,
          })
        }
      } else {
        const cellRef = getCellRef(
          currentTraverseMarker.rowIndex,
          nextCellIndex,
        )
        if (cellRef) {
          setActiveCell(currentTraverseMarker.rowIndex, nextCellIndex)
        }
      }
    }

    const handleVerticalMovement = () => {
      const nextRowIndex = isCtrlHeld
        ? getBoundary(
            "row",
            currentTraverseMarker.rowIndex,
            direction === "up" ? "first" : "last",
          )
        : ["up"].includes(direction)
          ? currentTraverseMarker.rowIndex - 1
          : currentTraverseMarker.rowIndex + 1

      if (nextRowIndex === undefined) return

      const nextCellIndex =
        isShiftHeld && currentShiftTraverseMarker
          ? currentShiftTraverseMarker.cellIndex
          : currentCellIndex

      if (nextCellIndex === undefined) return

      if (isShiftHeld && getCellRef(nextRowIndex, nextCellIndex)) {
        setSelectedCellRange({
          startRowIndex: currentRowIndex,
          startCellIndex: currentCellIndex,
          endRowIndex: nextRowIndex,
          endCellIndex:
            currentShiftTraverseMarker?.cellIndex ?? currentCellIndex,
        })
      } else if (getCellRef(nextRowIndex, nextCellIndex)) {
        setActiveCell(nextRowIndex, nextCellIndex)
      }
    }

    if (["left", "right"].includes(direction)) {
      handleHorizontalMovement()
    } else {
      handleVerticalMovement()
    }
  }

  // Selection

  const isSelectedCell = (rowIndex: number, cellIndex: number) => {
    return getCellState(rowIndex, cellIndex)?.isSelected ?? false
  }

  const getSelectedCells = () => {
    const selectedCells: Array<{ rowIndex: number; cellIndex: number }> = []
    cellsMap.current.forEach((row, rowIndex) => {
      row.forEach((cell, cellIndex) => {
        if (cell.isSelected) {
          selectedCells.push({ rowIndex, cellIndex })
        }
      })
    })
    return selectedCells
  }

  const addSelectedCell = (rowIndex: number, cellIndex: number) => {
    const cell = cellsMap.current.get(rowIndex)?.get(cellIndex)
    if (cell) {
      cell.isSelected = true
      cell.ref?.current?.setAttribute("data-is-selected", "true")
    }
  }

  const removeSelectedCell = (rowIndex: number, cellIndex: number) => {
    const cell = cellsMap.current.get(rowIndex)?.get(cellIndex)
    if (cell) {
      cell.isSelected = false
      cell.ref?.current?.setAttribute("data-is-selected", "false")
    }
  }

  const toggleSelectedCell = (rowIndex: number, cellIndex: number) => {
    if (isSelectedCell(rowIndex, cellIndex)) {
      removeSelectedCell(rowIndex, cellIndex)
    } else {
      addSelectedCell(rowIndex, cellIndex)
    }
  }

  const clearSelectedCells = () => {
    cellsMap.current.forEach((row) => {
      row.forEach((cell) => {
        cell.isSelected = false
        cell.ref?.current?.setAttribute("data-is-selected", "false")
      })
    })
  }

  const clearSelectedCellsValue = () => {
    const selectedCells = getSelectedCells()
    clearCellsValue(selectedCells)
  }

  const selectAllCells = () => {
    cellsMap.current.forEach((row, rowIndex) => {
      row.forEach((cell, cellIndex) => {
        addSelectedCell(rowIndex, cellIndex)
      })
    })
  }

  const selectAllCellsInRow = (rowIndex: number) => {
    cellsMap.current.get(rowIndex)?.forEach((cell, cellIndex) => {
      addSelectedCell(rowIndex, cellIndex)
    })
  }

  const selectAllCellsInColumn = (cellIndex: number) => {
    cellsMap.current.forEach((row, rowIndex) => {
      addSelectedCell(rowIndex, cellIndex)
    })
  }

  const setSelectedCellRange = ({
    startRowIndex,
    startCellIndex,
    endRowIndex,
    endCellIndex,
  }: {
    startRowIndex: number
    startCellIndex: number
    endRowIndex: number
    endCellIndex: number
  }) => {
    clearSelectedCells()

    const [minRow, maxRow] = [
      Math.min(startRowIndex, endRowIndex),
      Math.max(startRowIndex, endRowIndex),
    ]
    const [minCell, maxCell] = [
      Math.min(startCellIndex, endCellIndex),
      Math.max(startCellIndex, endCellIndex),
    ]

    for (let row = minRow; row <= maxRow; row++) {
      for (let cell = minCell; cell <= maxCell; cell++) {
        addSelectedCell(row, cell)
      }
    }

    setShiftTraverseMarker({ rowIndex: endRowIndex, cellIndex: endCellIndex })
  }

  // Mouse and Shift Selection

  const handleShiftClickCell = (rowIndex: number, cellIndex: number) => {
    const startCell = getActiveCell()
    if (!startCell) return

    setSelectedCellRange({
      startRowIndex: startCell.rowIndex,
      startCellIndex: startCell.cellIndex,
      endRowIndex: rowIndex,
      endCellIndex: cellIndex,
    })
  }

  const handleMouseSelectStart = (rowIndex: number, cellIndex: number) => {
    selectionState.current.mouseSelectStartCell = { rowIndex, cellIndex }

    window.addEventListener("pointerup", handleMouseSelectEnd)
  }

  const handleMouseSelectMove = (rowIndex: number, cellIndex: number) => {
    const startCell = selectionState.current.mouseSelectStartCell
    if (!startCell) return

    setSelectedCellRange({
      startRowIndex: startCell.rowIndex,
      startCellIndex: startCell.cellIndex,
      endRowIndex: rowIndex,
      endCellIndex: cellIndex,
    })
  }

  const handleMouseSelectEnd = () => {
    selectionState.current.mouseSelectStartCell = undefined
    window.removeEventListener("pointerup", handleMouseSelectEnd)
  }

  // Traverse Markers

  const getShiftTraverseMarker = ():
    | { rowIndex: number; cellIndex: number }
    | undefined => {
    return selectionState.current.shiftTraverseMarker
  }

  const startShiftTraverse = ({
    rowIndex,
    cellIndex,
  }: {
    rowIndex: number
    cellIndex: number
  }) => {
    // Don't clobber an existing shift selection
    if (!selectionState.current.shiftTraverseMarker) {
      setShiftTraverseMarker({ rowIndex, cellIndex })
    }
  }

  const setShiftTraverseMarker = ({
    rowIndex,
    cellIndex,
  }: {
    rowIndex: number
    cellIndex: number
  }) => {
    selectionState.current.shiftTraverseMarker = { rowIndex, cellIndex }
  }

  const clearShiftTraverseMarker = () => {
    selectionState.current.shiftTraverseMarker = undefined
  }

  // Initialization

  useEffect(() => {
    if (pendingInitializations.current.length > 0) {
      setCellValues((prev) => {
        const newValues = new Map(prev)
        pendingInitializations.current.forEach(
          ({ rowIndex, cellIndex, value }) => {
            if (!newValues.has(rowIndex)) {
              newValues.set(rowIndex, new Map())
            }
            newValues.get(rowIndex)?.set(cellIndex, value)
          },
        )
        return newValues
      })
      pendingInitializations.current = []
    }
  }, [])

  const contextValue = useMemo(
    () => ({
      // Cell Management
      getCellState,
      getRowMap,
      getCellsMap,
      setCellValue,
      addCellIndex,

      // Active Cell Management
      isActiveCell,
      setActiveCell,
      setInputFocus,

      // Navigation
      setNextActiveCell,

      // Selection
      isSelectedCell,
      toggleSelectedCell,
      clearSelectedCells,
      clearSelectedCellsValue,
      selectAllCells,
      selectAllCellsInRow,
      selectAllCellsInColumn,

      // Mouse and Shift Selection
      handleMouseSelectStart,
      handleMouseSelectMove,
      handleShiftClickCell,

      // Traverse Markers
      startShiftTraverse,
    }),
    [cellValues],
  )

  return (
    <CellsContext.Provider value={contextValue}>
      {children}
    </CellsContext.Provider>
  )
}

import {
  createContext,
  useContext,
  useMemo,
  useState,
  useCallback,
} from "react"
import { useCellsContext, type CellTraverseDirection } from "./use-cells"

interface CellContextType {
  cellValue: string
  isSelected: boolean
  isEditing: boolean
  isActive: boolean
  handleBlur: (e: React.FocusEvent<HTMLDivElement | HTMLInputElement>) => void
  handleFocus: (e: React.FocusEvent<HTMLDivElement | HTMLInputElement>) => void
  handleChange: (e: React.ChangeEvent<HTMLInputElement>) => void
  handleKeyDown: (
    e: React.KeyboardEvent<HTMLDivElement | HTMLInputElement>,
  ) => void
  handlePointerDown: (e: React.PointerEvent<HTMLInputElement>) => void
  handlePointerEnter: () => void
  handleDoubleClick: () => void
}

const CellContext = createContext<CellContextType>({
  cellValue: "",
  isSelected: false,
  isEditing: false,
  isActive: false,
  handleBlur: () => {},
  handleFocus: () => {},
  handleChange: () => {},
  handleKeyDown: () => {},
  handlePointerDown: () => {},
  handlePointerEnter: () => {},
  handleDoubleClick: () => {},
})

export const useCellContext = () => {
  const context = useContext(CellContext)

  if (!context) {
    throw new Error("useCellContext must be used within a CellContextProvider")
  }

  return context
}

export const CellContextProvider = ({
  rowIndex,
  cellIndex,
  children,
}: {
  rowIndex: number
  cellIndex: number
  children: React.ReactElement
}) => {
  const {
    setCellValue,
    setActiveCell,
    startShiftTraverse,
    clearSelectedCells,
    clearSelectedCellsValue,
    setInputFocus,
    setNextActiveCell,
    toggleSelectedCell,
    selectAllCells,
    selectAllCellsInRow,
    selectAllCellsInColumn,
    handleShiftClickCell,
    handleMouseSelectStart,
    getCellState,
    handleMouseSelectMove,
  } = useCellsContext()

  const [isEditing, setIsEditing] = useState(false)

  const cellState = getCellState(rowIndex, cellIndex)
  const cellValue = cellState?.value ?? ""
  const isSelected = cellState?.isSelected ?? false
  const isActive = cellState?.isActive ?? false

  const handleChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      const newValue = e.target.value
      setCellValue(rowIndex, cellIndex, newValue)
    },
    [rowIndex, cellIndex, setCellValue],
  )

  const handleBlur = useCallback(
    (e: React.FocusEvent<HTMLDivElement | HTMLInputElement>) => {
      if (e.target !== document.activeElement) {
        const elmType = (e.target as HTMLElement).tagName.toLowerCase()
        if (elmType === "div") {
          clearSelectedCells()
        } else if (elmType === "input") {
          setIsEditing(false)
        }
      }
    },
    [clearSelectedCells],
  )

  const handleFocus = useCallback(
    (e: React.FocusEvent<HTMLDivElement | HTMLInputElement>) => {
      const elmType = (e.target as HTMLElement).tagName.toLowerCase()
      if (elmType === "input") {
        setIsEditing(true)
      }
    },
    [],
  )

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent<HTMLDivElement | HTMLInputElement>) => {
      const elmType = (e.target as HTMLElement).tagName.toLowerCase()

      if (elmType === "input") {
        switch (e.key) {
          case "Escape":
            setActiveCell(rowIndex, cellIndex)
            return
          case "Enter":
          case "F2":
            setActiveCell(rowIndex, cellIndex)
            return
        }
      }

      if (elmType === "div") {
        if (e.target !== e.currentTarget) return
        if (rowIndex === undefined || cellIndex === undefined) return

        const keyPressed = e.key

        const keyMap: Record<string, CellTraverseDirection> = {
          ArrowLeft: "left",
          ArrowRight: "right",
          ArrowUp: "up",
          ArrowDown: "down",
        }

        const isAlphaNumeric = /^[a-zA-Z0-9]$/.test(keyPressed)

        switch (keyPressed) {
          case "Space":
          case " ":
            if (e.shiftKey) {
              e.preventDefault()
              selectAllCellsInRow(rowIndex)
            } else if (e.ctrlKey || e.metaKey) {
              e.preventDefault()
              selectAllCellsInColumn(cellIndex)
            }
            return

          case "Shift":
            startShiftTraverse({
              rowIndex,
              cellIndex,
            })
            return

          case "Escape":
            clearSelectedCells()
            setActiveCell(rowIndex, cellIndex)
            return

          case "Enter":
          case "F2":
            setInputFocus(rowIndex, cellIndex)
            return

          case "Delete":
          case "Backspace":
            setCellValue(rowIndex, cellIndex, "")
            clearSelectedCellsValue()
            return

          case "Home":
            e.preventDefault()
            setNextActiveCell({
              direction: "left",
              currentRowIndex: rowIndex,
              currentCellIndex: cellIndex,
              isCtrlHeld: true,
            })
            return

          case "End":
            e.preventDefault()
            setNextActiveCell({
              direction: "right",
              currentRowIndex: rowIndex,
              currentCellIndex: cellIndex,
              isCtrlHeld: true,
            })
            return

          case "PageUp":
            e.preventDefault()
            setNextActiveCell({
              direction: "up",
              currentRowIndex: rowIndex,
              currentCellIndex: cellIndex,
              isCtrlHeld: true,
            })
            return

          case "PageDown":
            e.preventDefault()
            setNextActiveCell({
              direction: "down",
              currentRowIndex: rowIndex,
              currentCellIndex: cellIndex,
              isCtrlHeld: true,
            })
            return

          case "a":
            if (e.ctrlKey || e.metaKey) {
              e.preventDefault()
              selectAllCells()
            }
            return
        }

        const direction = keyMap[keyPressed]

        if (direction) {
          e.preventDefault()

          setNextActiveCell({
            direction,
            currentRowIndex: rowIndex,
            currentCellIndex: cellIndex,
            isShiftHeld: e.shiftKey,
            isCtrlHeld: e.ctrlKey || e.metaKey,
          })
        }

        if (isAlphaNumeric) {
          setInputFocus(rowIndex, cellIndex)
        }
      }
    },
    [
      rowIndex,
      cellIndex,
      startShiftTraverse,
      clearSelectedCells,
      setActiveCell,
      setInputFocus,
      setCellValue,
      setNextActiveCell,
    ],
  )

  const handlePointerDown = useCallback(
    (e: React.PointerEvent<HTMLInputElement>) => {
      if (e.ctrlKey || e.metaKey) {
        e.preventDefault()
        toggleSelectedCell(rowIndex, cellIndex)
        return
      } else if (e.shiftKey) {
        e.preventDefault()
        handleShiftClickCell(rowIndex, cellIndex)
        return
      } else {
        e.preventDefault()
        setActiveCell(rowIndex, cellIndex)
        handleMouseSelectStart(rowIndex, cellIndex)
      }
    },
    [
      rowIndex,
      cellIndex,
      toggleSelectedCell,
      handleShiftClickCell,
      setActiveCell,
      handleMouseSelectStart,
    ],
  )

  const handleDoubleClick = useCallback(() => {
    setInputFocus(rowIndex, cellIndex)
  }, [rowIndex, cellIndex, setInputFocus])

  const handlePointerEnter = useCallback(() => {
    handleMouseSelectMove(rowIndex, cellIndex)
  }, [rowIndex, cellIndex, handleMouseSelectMove])

  const contextValue = useMemo(
    () => ({
      cellValue,
      isSelected,
      isEditing,
      isActive,
      handleBlur,
      handleFocus,
      handleChange,
      handleKeyDown,
      handlePointerDown,
      handlePointerEnter,
      handleDoubleClick,
    }),
    [
      cellValue,
      isSelected,
      isEditing,
      isActive,
      handleBlur,
      handleFocus,
      handleChange,
      handleKeyDown,
      handlePointerDown,
      handlePointerEnter,
      handleDoubleClick,
    ],
  )

  return (
    <CellContext.Provider value={contextValue}>{children}</CellContext.Provider>
  )
}
